$(document).ready(function(){link_form_override();upload_form_override();setInterval(function(){update_playlist()},1500)});function link_form_override(){$("#queue").submit(function(b){b.preventDefault();$("#queuebutton").val("Submitting...");b={video_link:$("input[name=video_link]").val()};$.ajax({type:"POST",url:"/ajax/queue",data:b}).done(function(a){$("#queuebutton").val("Go");$("#queueinput").val("");$("#queue").notify(a.Message,a.Type)})})}function upload_form_override(){$("#upload").submit(function(b){b.preventDefault();$("#filebutton").val("Submitting...");$("#filebutton").attr("disabled",!0);$.ajax({type:"POST",url:"/ajax/upload",data:new FormData($("#upload")[0]),cache:!1,processData:!1,contentType:!1,xhr:function(){var a=$.ajaxSettings.xhr();a.upload&&a.upload.addEventListener("progress",function(a){a.lengthComputable&&(a=Math.round(a.loaded/a.total*100)+"%",$("#meter").css("width",a))},!1);return a}}).done(function(a){$("#filebutton").attr("disabled",!1);$("#filebutton").val("Go");$("#fileinput").val("");$("#meter").css("width","0%");$("#upload").notify(a.Message,a.Type)})})}function update_playlist(){$("#main").load("/ajax/playlist")};